
public class square {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 225;
		double b = Math.sqrt(a);
		System.out.println(b);
		
		//Another way
		int c = 225;
		System.out.println(Math.sqrt(c));
		
		//Last way
		System.out.println(Math.sqrt(225));
	}

}
